import { Component, Input, OnInit, Injectable } from '@angular/core';

import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

import { Subject, BehaviorSubject, Observable } from 'rxjs';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})

@Injectable({
  providedIn: 'root'
})

export class HomeComponent implements OnInit {

  private GridHeaderList = [
    { Column1: '', Column2: '', Column3: '', Column4: '', Column5: '' }
  ];

  @Input() title2: string;
  // GridHeaderList2: any = [];
  employeeList: any = [];


  data$ = this.GridHeaderList.asObservable();

  sendData(data: string) {
    this.GridHeaderList.next(data);
  }

  constructor(private http : HttpClient) {
  this.title2="Users"
  // this.GridHeaderList2 = [
  //   { Column1: 'FirstName', Column2: 'LastName', Column3: 'Email', Column4: 'Address', Column5: 'Phone' }
  // ];
  }

  ngOnInit(): void {
    this.getEmp();    
  }

  getEmp() {
    // title="Users"

    this.GridHeaderList = [
      { Column1: 'FirstName', Column2: 'LastName', Column3: 'Email', Column4: 'Address', Column5: 'Phone' }
    ];

    this.http.get('http://localhost:3000/employee').subscribe(data=>{
      this.employeeList = data;
    })
  }
}
