import { Component, OnInit } from '@angular/core';

import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

import { Medias } from 'src/app/gridcontent';
import { Title } from '@angular/platform-browser';

@Component({
  selector: 'app-media',
  templateUrl: './media.component.html',
  styleUrls: ['./media.component.css']
})

export class MediaComponent implements OnInit  {

  GridHeaderList = [
    { Column1: '', Column2: '', Column3: '', Column4: '', Column5: '' }
  ];

  mediaList: any = [];
  // mediaList = new Array();
  test1: string = "";
  tempList: any = [];
  filterTxt: string = "";

  constructor(private http : HttpClient) {

  }

  ngOnInit(): void {
    this.getMedia(this.filterTxt);
  }

  getMedia(filterTxt: string) {
    this.GridHeaderList = [
      { Column1: 'Name', Column2: 'Type', Column3: 'Location', Column4: 'Phone', Column5: '' }
    ];

    this.http.get('http://localhost:9000/media').subscribe(data=>{
      // this.mediaList = data;
      this.tempList = data;
      if(filterTxt === "")
      {
        this.mediaList = data;
      }
      else
      {
        this.mediaList = this.tempList.filter((media: { Id: string; }) => media.Id === filterTxt);
      }
    });

  }

  filterMedia(filterTxt: string) {
    // this.getMedia(filterTxt);
    // this.mediaList = this.tempList.filter((media: { Id: string; }) => media.Id === filterTxt)
    this.filterTxt = filterTxt;
  }
}
